import React, {useEffect} from 'react'
import "./skills.scss"

export default function ProgressBar({label, width, percent}) {
    const [value, setValue] = React.useState(0);
    useEffect(() => {
      setValue(percent * width);
    }, [percent, width]);

    return (
      <div>
        <div className="bar">
          <span className="bar-label">{label}</span>
          <span className="bar-fill" style={{ width: `${value}px` }}/>
        </div>
      </div>
    );
}
